    </div>
</template>

<script>
import { mapGetters, mapActions } from 'vuex';
import Header from '../Header.vue';
import Footer from '../Footer.vue';
import BackBtn from '../BackBtn.vue';

import { Icon } from '@iconify/vue';
import GameControls from './GameControls.vue';
import Gallery from './Gallery.vue';

export default {
    name: 'ProjectDetailContent',

    inject: ['emitter'],
    emits: [],

    props: {

    },

    components: {
        Header,
        Footer,
        BackBtn,
        GameControls,
        Gallery,
        Icon
    },

    data() {
        return {
            projectData: null,
            projectType: null,

            isViewGame: false
        }
    },

    methods: {
        ...mapActions(
            {

            }
        ),

        getProjectDetail() {
            const projId = this.$route.params.projectId;
            const projects = this.getProjects;
            this.projectData = projects.find(proj => proj.id == projId);
            this.projectType = this.projectData.category;
        },

        openGameFullscreen() {
            if (!this.isViewGame) return;
            const elem = document.getElementById("game-iframe");
            if (elem.requestFullscreen) {
                elem.requestFullscreen();
            } else if (elem.webkitRequestFullscreen) {
                elem.webkitRequestFullscreen();
            } else if (elem.msRequestFullscreen) {
                elem.msRequestFullscreen();
            }
        },
        
        toggleControls(event) {
            const el = event.target;
            if (el.classList.contains("open")) el.classList.remove("open");
            else el.classList.add("open");
        }
    },
    
    computed: {
        ...mapGetters(
            {
                getProjects: "project/getProjects",
                IS_MOBILE: "misc/getIsMobile"
            }
        ),
    },

    created() {
        window.scrollTo({ top: 0, left: 0, behavior: "instant" });
        this.getProjectDetail();
    },

    mounted() {

    }
}
</script>

